getBuskerList:
SELECT user.user_id, image.path, busker.nick_name,  busker.date_of_birth,busker.sex, busker.instruments, busker.introduction
FROM `user`, busker , image
where user.user_id = busker.busker_id and `user`.icon_path = image.image_id;

getTrailList:
SELECT trail_notice.trail_id, trail_notice.perform_time, trail_notice.participant, trail_notice.perform_address,trail_notice.describe, image.path
FROM trail_notice, image
WHERE trail_notice.poster = image.image_id;

getImagePathByUsername:
SELECT image.path 
FROM  image, `user`
WHERE  image.image_id = `user`.icon_path AND `user`.username = ?;
